import React, { Component } from 'react'
import { Typography } from '@material-ui/core';
import { List } from '@material-ui/core';
import { ListItem } from '@material-ui/core';
import { ListItemAvatar } from '@material-ui/core';
import { Avatar } from '@material-ui/core';
import { ListItemText } from '@material-ui/core';
import PublicIcon from '@material-ui/icons/Public';
import FlagIcon from '@material-ui/icons/Flag';
import { Box } from '@material-ui/core';
import { AppBar } from '@material-ui/core';
import { Toolbar } from '@material-ui/core';
import EuroSymbolIcon from '@material-ui/icons/EuroSymbol';
import { Link } from 'react-router-dom';

import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import { Icon } from "leaflet";
import 'leaflet/dist/leaflet.css';
import markerIconPng from "leaflet/dist/images/marker-icon.png"

//import dataJson from "./fuentes.json";
import { useEffect } from 'react';
import axios from 'axios';


const Home = () => {

    const [data, setData] = React.useState(null);

    useEffect(() => {

        var body = "data=[out:json];node[amenity = drinking_water](around: 6000, 48.864716, 2.349);out;"
        var url = "http://overpass-api.de/api/interpreter";

        axios({
            method: "post",
            url: url,
            data: body,
        })
            .then(function (response) {
                //console.log(response);
                setData(response.data.elements)
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })

    }, []);

    const position = [48.864716, 2.349];// Paris position
    //console.log(dataJson.elements);



    return (
        <div >
            <Box style={{ height: "100vh" }}>
                <MapContainer center={position} zoom={13} scrollWheelZoom={false}>
                    <TileLayer
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />


                    {data.map((element) =>{
                        var positionEle = [element.lat, element.lon];
                            return(
                                <Marker
                                    position={positionEle}
                                    icon={new Icon({ iconUrl: markerIconPng, iconSize: [25, 41], iconAnchor: [12, 41] })}
                                >
                                </Marker>
                            )
                        })

                    }
                </MapContainer>
            </Box>
        </div>
    )

}

export default Home
